template.script:
            buyer = document.getElementById('buyer')
            supplier = document.getElementById('seller')
            both = document.getElementById('both')

            function check_email(element) {
                const email = document.getElementById('login_email').value;
                const vals = {
                    'email': email
                };
                fetch('/checkEmail', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(vals),
                })
                .then(function(res) {
                    return res.json();  // Parse the JSON response
                })
                .then(function(data) {
                    
                    if (data.result[0].status === 'error') {
                        if(document.getElementById('login_email').nextElementSibling){
                            document.getElementById('login_email').nextElementSibling.removeChild(document.getElementById('login_email').nextElementSibling.firstChild) 
                            
                        }
                        alert(data.result[0].message);
                        const message = document.createElement('div');
                        message.style.color = 'red'
                        message.innerText = data.result[0].message
                        document.getElementById('login_email').parentElement.appendChild(message)
                    } else {
                        if(document.getElementById('login_email').nextElementSibling){
                            document.getElementById('login_email').nextElementSibling.removeChild(document.getElementById('login_email').nextElementSibling.firstChild) 
                        }
                        const message = document.createElement('div');
                        message.style.color = 'green'
                        message.innerText = data.result[0].message
                        document.getElementById('login_email').parentElement.appendChild(message)
                        document.getElementById('send_otp_btn').parentElement.style.display = 'block'
                    }
                })
                .catch(function(error) {
                    console.error('Error:', error);    // Handle network or other fetch errors
                });
            }
            function send_otp(element){
                document.getElementById('otp_text').parentElement.parentElement.style.display = 'block'
                document.getElementById('verify_otp_btn').parentElement.style.display = 'block'
                document.getElementById('send_otp_btn').innerText = 'Resend OTP'
                const email = document.getElementById('login_email').value
                const vals = {
                    'email':email
                }
                fetch('/generate_otp', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(vals),
                }).then(function(res) {
                    return res.json();  // Parse the JSON response
                }).then(function(data) {
                    console.log(data);
                    // handle response or reload page
                    if (data.result[0].status === 'error') {
                        alert("Something went wrong please try again later"); 
                    }
                    else {
                        document.getElementById('otp_text').parentElement.parentElement.style.display = 'block'
                    }
                }).catch(function(error) {
                    console.error('Error:', error);    // Handle network or other fetch errors
                });

            }
            function verify_otp(element){
                const otp = document.getElementById('otp_text').value
                const email = document.getElementById('login_email').value
                
                const vals = {
                    'email':email,
                    'otp':otp
                }
                fetch('/verify_otp', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(vals),
                }).then(function(res) {
                    return res.json();  // Parse the JSON response
                }).then(function(data) {
                    console.log(data);
                    // handle response or reload page
                    
                    if (data.result.status === 'error') {
                        alert("Something went wrong please try again later"); 
                    }
                    else {
                        document.getElementById('send_otp_btn').parentElement.style.display = 'none'
                        document.getElementById('verify_otp_btn').parentElement.style.display = 'none'
                        document.getElementById('password').parentElement.parentElement.style.display = 'block'
                        document.getElementById('otp_text').parentElement.parentElement.style.display = 'none'
                        document.getElementById('confirm_password').parentElement.parentElement.style.display = 'block'
                        document.getElementById('submit').parentElement.style.display = 'block'
                    }
                }).catch(function(error) {
                    console.error('Error:', error);    // Handle network or other fetch errors
                });;
            }

            function role_changed(){
                if(buyer.checked == true){
                    document.getElementById('buyer_textarea').parentElement.parentElement.style.display = 'block'
                    document.getElementById('supplier_textarea').parentElement.parentElement.style.display = 'none'
                    document.getElementById('trader_textarea').parentElement.parentElement.style.display = 'none'

                }
                if(supplier.checked == true){
                    document.getElementById('supplier_textarea').parentElement.parentElement.style.display = 'block'
                    document.getElementById('buyer_textarea').parentElement.parentElement.style.display = 'none'
                    document.getElementById('trader_textarea').parentElement.parentElement.style.display = 'none'
                }
                if(both.checked == true){
                    document.getElementById('buyer_textarea').parentElement.parentElement.style.display = 'block'
                    document.getElementById('supplier_textarea').parentElement.parentElement.style.display = 'block'
                    document.getElementById('trader_textarea').parentElement.parentElement.style.display = 'block'
                }
            }

            



                function close_tnc(){
                    document.getElementById('terms_condition_container').style.display = 'none'
                    
                }
                function open_tnc(){
                    document.getElementById('terms_condition_container').style.display = 'block'
                    
                }

                function open_privacypolicy(){
                    document.getElementById('privacypolicy_container').style.display = 'block'
                    
                }
                function close_privacypolicy(){
                    document.getElementById('privacypolicy_container').style.display = 'none'
                    
                }

                


                document.getElementById('form1').classList.add('active')
                
                function switchForm(currentForm, nextForm) {
                    let child_len = currentForm.querySelectorAll('input , textarea, select').length
                    let page_load = false


                    if(document.getElementById('buyer').checked){
                        document.getElementById('buyer_textarea').required  = true
                        document.getElementById('supplier_textarea').required  = false
                        document.getElementById('supplier_textarea').style.display  = 'none'
                    }else if(document.getElementById('seller').checked){
                        document.getElementById('supplier_textarea').required  = true
                        document.getElementById('buyer_textarea').required  = false
                        document.getElementById('buyer_textarea').style.display  = 'none'
                    }else {
                        document.getElementById('buyer_textarea').style.display  = 'block'
                        document.getElementById('supplier_textarea').style.display  = 'block'
                        document.getElementById('supplier_textarea').required  = true
                        document.getElementById('buyer_textarea').required  = true
                    } 

                    currentForm.querySelectorAll('input , textarea,select').forEach(function(element){
                        element.style.outline = 'none'
                    })
                    


                    


                    for(let i = 0;i < child_len ;i++){
                        
                        if(currentForm.querySelectorAll('input , textarea, select')[i].required && currentForm.querySelectorAll('input , textarea , select')[i].value == ""){
                            currentForm.querySelectorAll('input , textarea, select')[i].style.outline = '2px solid red'
                            page_load = false

                        }else{
                            
                            page_load = true
                        }
                    }
                    
                    // Fade out the current form
                    if(!page_load){
                        return
                    }
                    
                    else {
                        currentForm.classList.remove('active');
                        // After the fade-out transition, hide currentForm and show nextForm
                    setTimeout(function() {
                        currentForm.style.display = 'none';
                        nextForm.style.display = 'block';
                        
                        // Allow the browser to apply the display change before fading in
                        setTimeout(function() {
                            nextForm.classList.add('active');
                        }, 10); // Small delay to ensure display property is applied
                    }, 500); // Match the transition duration in CSS
                    }
                }

                // Event listeners for the navigation buttons
                document.getElementById('next1').onclick = function() {
                    switchForm(document.getElementById('form1'), document.getElementById('form2'));
                };

                document.getElementById('prev1').onclick = function() {
                    switchForm(document.getElementById('form2'), document.getElementById('form1'));
                };

                document.getElementById('next2').onclick = function() {
                    switchForm(document.getElementById('form2'), document.getElementById('form3'));
                };

                document.getElementById('prev2').onclick = function() {
                    switchForm(document.getElementById('form3'), document.getElementById('form2'));
                };

                function fetch_state(element){
                    
                    const country_id = element.value
                    const state_dropdown = document.getElementById('state')
                    const vals = {
                        'country_id':country_id
                    }
                    fetch('/get_states',{
                            method: 'POST',
                            headers: {
                                'Content-Type': 'application/json',
                            },
                            body: JSON.stringify(vals),
                        }).then(function(resp){
                            resp = resp.json().then(function(jsonData){
                            states_list = jsonData.result.state_list
                            while (state_dropdown.firstChild) {
                                state_dropdown.removeChild(state_dropdown.firstChild);
                            }
                            const option1 = document.createElement('option');
                            option1.value = ''
                            option1.textContent = ''
                            states_list.forEach(function(state_element) {
                                const option = document.createElement('option');
                                    option.value = state_element.id
                                    option.textContent = state_element.name;
                                    
                                    state_dropdown.appendChild(option)
                            })
                                

                            })
                        }).catch(function(error){
                            console.log(error)
                        })

                    
                }
