function buyer_notifications(element){
    const buyer_notifications = document.getElementById('buyer_notifications')
    const seller_notifications = document.getElementById('seller_notifications')
    element.classList.add('active')
    element.nextElementSibling.classList.remove('active');

    if(seller_notifications.style.display == 'block'){
        seller_notifications.style.display = 'none'
        buyer_notifications.style.display = 'block'

    }else{
        seller_notifications.style.display = 'block'
        buyer_notifications.style.display = 'none'

    }
}

function seller_notifications(element){
    const buyer_notifications = document.getElementById('buyer_notifications')
    const seller_notifications = document.getElementById('seller_notifications')
    element.classList.add('active')
    element.previousElementSibling.classList.remove('active');

    if(buyer_notifications.style.display == 'block'){
        buyer_notifications.style.display = 'none'
        seller_notifications.style.display = 'block'

    }else{
        seller_notifications.style.display = 'none'
        buyer_notifications.style.display = 'block'

    }
}

function product_image_change(element) {
    console.log("Image file selected");
    debugger;
    const file = element.files[0];
    if (file) {
        const reader = new FileReader();
        reader.onload = function(e) {
            const imgElement = element.parentNode.previousElementSibling;
            if (imgElement && imgElement.tagName === 'IMG') {
                imgElement.src = e.target.result;
                console.log("Image source updated to:", e.target.result);
            } else {
                console.log("Image element not found or not an IMG tag.");
            }
        };
        reader.readAsDataURL(file);
    } else {
        console.log("No file selected.");
    }
}


function save_edited_product(element) {
    const productId = element.getAttribute('ProductId');
    debugger;
    const product_description = document.getElementById('edit-product_description').value;
    const product_name = document.getElementById('edit-product_name').value;
    const product_quantity = document.getElementById('edit-product_quantity').value;
    const packaging_requirement = document.getElementById('edit-packaging_requirement').value;
    const delivery_days = document.getElementById('edit-delivery_days').value;
    const product_price_usd = document.getElementById('edit-product_price_usd').value;
    const payment_mode = document.getElementById('edit-payment_mode').value;
    const ready_to_ship = document.getElementById('edit-ready_to_buy').checked;
    const rts_quantity = document.getElementById('edit-rts_quantity').value;
    const sample_policy = document.getElementById('edit-sample_policy').value;

    const product_image_input = document.getElementById('update_product_image');
    let product_image = null;

    if (product_image_input.files && product_image_input.files[0]) {
        const reader = new FileReader();
        reader.onloadend = function() {
            product_image = reader.result.split(',')[1]; // Get base64 encoded image

            const vals = {
                'product_description': product_description,
                'product_name': product_name,
                'product_quantity': product_quantity,
                'packaging_requirement': packaging_requirement,
                'delivery_days': delivery_days,
                'product_price_usd': product_price_usd,
                'payment_mode': payment_mode,
                'ready_to_ship': ready_to_ship,
                'rts_quantity': rts_quantity,
                'sample_policy': sample_policy,
                'product_image': product_image
            };

            fetch('/profile/updateProduct/' + productId, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(vals),
            }).then(function(res) {
                console.log(res);
                // handle response or reload page
            });
        };
        reader.readAsDataURL(product_image_input.files[0]);
    } else {
        const vals = {
            'product_description': product_description,
            'product_name': product_name,
            'product_quantity': product_quantity,
            'packaging_requirement': packaging_requirement,
            'delivery_days': delivery_days,
            'product_price_usd': product_price_usd,
            'payment_mode': payment_mode,
            'ready_to_ship': ready_to_ship,
            'rts_quantity': rts_quantity,
            'sample_policy': sample_policy
        };

        fetch('/profile/updateProduct/' + productId, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(vals),
        }).then(function(res) {
            console.log(res);
            // handle response or reload page
        });
    }
}


