function end_rfq(element){
    const rfqId = element.getAttribute('rfqId')
    const vals = {
        'rfqId':rfqId
    }
    fetch('/endRfq',{
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body:JSON.stringify(vals)

    }).then(function(res){
        window.location.href = '/profile'
    })

}

