function addUser(element){
    let table_body = document.getElementById('employee_table').children[1]
    let table_length = table_body.children.length
    const newRow = document.createElement('tr');

    const deleteCell = document.createElement('td');
    const nameCell = document.createElement('td');
    const designationCell = document.createElement('td');
    const contactCell = document.createElement('td');
    const emailCell = document.createElement('td');



    const deleteButton = document.createElement('button');
    deleteButton.type = 'button';
    deleteButton.style = 'background:transparent;border:none;';
    deleteButton.onclick = function() {
        openDeleteUser(deleteButton);
    };

    // Create the SVG icon and append it to the button
    deleteButton.innerHTML = `

    `;



    row_cnt = Number(table_length)+1

    const userName = document.createElement('input');
    userName.type = 'text'
    userName.id = 'user_'+row_cnt
    userName.name = 'new_user_'+row_cnt
    userName.style.display = 'block'

    const userDesignation = document.createElement('input');
    userDesignation.type = 'text'
    userDesignation.id = 'user_designation_'+row_cnt
    userDesignation.name = 'new_user_designation_'+row_cnt
    userDesignation.style.display = 'block'

    const userContact = document.createElement('input');
    userContact.type = 'text'
    userContact.id = 'user_contact_'+row_cnt
    userContact.name = 'new_user_contact'+row_cnt
    userContact.style.display = 'block'

    const userEmail = document.createElement('input');
    userEmail.type = 'text'
    userEmail.id = 'user_email_'+row_cnt
    userEmail.name = 'new_user_email'+row_cnt
    userEmail.style.display = 'block'



    const user_name  = document.getElementById('user_name');
    const user_designation = document.getElementById('user_designation');
    const user_contact = document.getElementById('user_contact');
    const user_email = document.getElementById('user_email');



    if(user_name.value.trim() != '' && user_contact.value.trim() != '' && user_name != null && user_contact != null){
        userName.value = user_name.value
        userDesignation.value = user_designation.value
        userContact.value = user_contact.value
        userEmail.value = user_email.value


    }
    else{
        alert("Name or contact cannot be empty.")
    }
    deleteCell.appendChild(deleteButton)
    nameCell.appendChild(userName)
    designationCell.appendChild(userDesignation)
    contactCell.appendChild(userContact)
    emailCell.appendChild(userEmail)


    newRow.appendChild(deleteCell)
    newRow.appendChild(nameCell)
    newRow.appendChild(designationCell)
    newRow.appendChild(contactCell)
    newRow.appendChild(emailCell)


    table_body.appendChild(newRow);


}

function addAward(element){
    let table_body = document.getElementById('award_table').children[1]
    let table_length = table_body.children.length
    const newRow = document.createElement('tr');
    const nameCell = document.createElement('td');




    const deleteButton = document.createElement('button');
    deleteButton.type = 'button';
    deleteButton.style = 'background:transparent;border:none;';

    deleteButton.onclick = function() {
        openDeleteAward(deleteButton);
    };

    // Create the SVG icon and append it to the button
    deleteButton.innerHTML = `

    `;
    const awardCell = document.createElement('td');
    const awardFile = document.createElement('td');
    const awardDescription = document.createElement('td');


    row_cnt = Number(table_length)+1

    const award_attachment = document.createElement('input');
    award_attachment.type = 'file'
    award_attachment.id = 'award_'+row_cnt
    award_attachment.name = 'new_award_'+row_cnt
    award_attachment.style.display = 'block'
    const fileNameSpan = document.createElement('span');
    const fileNameInput = document.createElement('input');
    fileNameInput.style.border = 'none'
    const fileDescriptionText = document.createElement('textarea');





    const originalInput  = document.getElementById('add_award');
    const add_award_name = document.getElementById('add_award_name');
    const award_name = document.getElementById('award_name');
    const award_description = document.getElementById('award_description');


    if (originalInput.files.length > 0 && award_name.value != '' ) {
        const file = originalInput.files[0];
        const dataTransfer = new DataTransfer();
        dataTransfer.items.add(file);

        award_attachment.files = dataTransfer.files;

        fileNameInput.setAttribute('name','new_name_'+row_cnt)
        fileNameInput.value = award_name.value
        fileDescriptionText.setAttribute('name','new_description_'+row_cnt)
        fileDescriptionText.value = award_description.value


    } else {
        alert('No file selected in the original input.');
        return
    }





    awardCell.appendChild(award_attachment)

    awardCell.appendChild(fileNameInput);


    awardFile.appendChild(award_attachment);
    awardDescription.appendChild(fileDescriptionText);






    originalInput.value = '';
    add_award_name.textContent = 'No File Selected' 
    award_name.value = ''
    award_description.value = ''

    nameCell.appendChild(deleteButton);
    newRow.appendChild(nameCell);
    newRow.appendChild(awardCell);
    newRow.appendChild(awardDescription);
    newRow.appendChild(awardFile);


    table_body.appendChild(newRow);

}

