function select_subcategory(element) {
    let chip_container = document.getElementById('selected-chips-container');
    let hiddenInput = document.getElementById('subscribed_categories_input'); // Make sure the ID exists in your input element

    let chip_box = document.createElement('span');
    let chip = document.createElement('span');
    let removeButton = document.createElement('button');

    // Add the 'x' for delete
    removeButton.innerText = 'x';
    removeButton.classList.add('remove-chip');
    removeButton.type = 'button';
    chip.classList.add('chip');
    chip_box.setAttribute('name', 'chipbox' + element.selectedOptions[0].value);
    chip_box.setAttribute('id', 'cb' + element.selectedOptions[0].value);

    // Set the chip text to the selected subcategory label
    chip.innerText = element.selectedOptions[0].label;
    chip.setAttribute('id', element.selectedOptions[0].value);

    // Append chip and delete button to the chip_box
    chip_box.appendChild(chip);
    chip_box.appendChild(removeButton);

    // Add class to style chip_box
    chip_box.classList.add('chipbox');

    // Append chip_box to the chip container
    chip_container.appendChild(chip_box);

    // Update hidden input when adding a new chip
    updateHiddenInputAdd(hiddenInput, element.selectedOptions[0].value);

    // Add event listener to remove the chip when 'x' is clicked
    removeButton.addEventListener('click', function() {
        removeChip(removeButton);
        updateHiddenInputRemove(hiddenInput, element.selectedOptions[0].value);
    });
}

function updateHiddenInputAdd(hiddenInput, id) {
    let currentValue = hiddenInput.value ? hiddenInput.value.split(',') : [];

    // Check if the ID is not already present
    if (!currentValue.includes(id)) {
        currentValue.push(id);  // Add the new ID
        hiddenInput.value = currentValue.join(',');  // Update the hidden input
    }
}

function updateHiddenInputRemove(hiddenInput, id) {
    let currentValue = hiddenInput.value ? hiddenInput.value.split(',') : [];

    // Correctly filter out the id
    currentValue = currentValue.filter(function(existingId) {
        return existingId !== id;  // Return true for IDs to keep, false for the one to remove
    });

    hiddenInput.value = currentValue.join(',');  // Update the hidden input
}

function removeChip(button) {
    let chip_box = button.parentElement;
    chip_box.remove();
}
function removeChipp(button){
    const category_id = button.parentElement.id.match(/\d+/g)[0]
    let chip_box = button.parentElement;
    const hidden_input = button.parentElement.parentElement.firstElementChild

    updateHiddenInputRemove(hidden_input,category_id)
    chip_box.remove();
}
