template.script:
    document.getElementById('company_details').style.display = 'flex'
    document.getElementById('contact_details').style.display = 'flex'
    document.getElementById('new_product').style.display = 'none'

function enable_documents(element){
    document.getElementById('documents').style.display = 'flex'
    document.getElementById('company_details').style.display = 'none'
    document.getElementById('contact_details').style.display = 'none'
    document.getElementById('new_product').style.display = 'none'


    document.querySelectorAll('.menu-item').forEach(function(item) {
            item.classList.remove('active');
        });

        // Add active class to the clicked menu item
        element.classList.add('active');
    }





function enable_profile_detail(){
    document.getElementById('documents').style.display = 'none'
    document.getElementById('new_product').style.display = 'none'
    document.getElementById('product_management').style.display = 'none'


    document.getElementById('company_details').style.display = 'flex'
    document.getElementById('contact_details').style.display = 'flex'

    document.querySelectorAll('.menu-item').forEach(function(item) {
            item.classList.remove('active');
        });

        // Add active class to the clicked menu item
        element.classList.add('active');


}
function enable_add_new_product(element){
    document.getElementById('documents').style.display = 'none'
    document.getElementById('company_details').style.display = 'none'
    document.getElementById('contact_details').style.display = 'none'
    document.getElementById('new_product').style.display = 'block'


    document.querySelectorAll('.menu-item').forEach(function(item) {
            item.classList.remove('active');
        });

        // Add active class to the clicked menu item
        element.classList.add('active');

}

function enable_product_management(element){
    document.getElementById('documents').style.display = 'none'
    document.getElementById('company_details').style.display = 'none'
    document.getElementById('contact_details').style.display = 'none'
    document.getElementById('product_management').style.display = 'flex'


    document.querySelectorAll('.menu-item').forEach(function(item) {
            item.classList.remove('active');
        });

        // Add active class to the clicked menu item
        element.classList.add('active');

}

function enable_queries(element){
    document.querySelectorAll('.menu-item').forEach(function(item) {
            item.classList.remove('active');
        });

        // Add active class to the clicked menu item
        element.classList.add('active');

}


document.querySelectorAll('.file-input').forEach(function(input) {
    input.addEventListener('change', function() {
        // Get the corresponding file name display span
        const fileNameSpan = document.getElementById(this.getAttribute('data-file-name'));
        // Update the file name display
        fileNameSpan.textContent = this.files[0] ? this.files[0].name : 'No file chosen';
    });
});

// Select all delete buttons and attach event listeners
document.querySelectorAll('.file-delete').forEach(function(deleteButton) {
    deleteButton.addEventListener('click', function() {
        // Get the corresponding file input
        const fileInput = document.querySelector(`input[data-delete-btn="${this.id}"]`);
        // Reset the file input
        fileInput.value = '';
        // Get the corresponding file name display span
        const fileNameSpan = document.getElementById(fileInput.getAttribute('data-file-name'));
        // Reset the file name display
        fileNameSpan.textContent = 'No file chosen';
    });
});
