function news_image_change(element){
    const file = element.files[0];
    if (file) {
        const reader = new FileReader();
        reader.onload = function(e) {
            const imgElement = element.parentNode.previousElementSibling;
            if (imgElement && imgElement.tagName === 'IMG') {
                imgElement.src = e.target.result;
            }
        };
        reader.readAsDataURL(file);
    }
}
function news_image_delete(element) {
    const parentNode = element.closest('.news-image-container');
    const imgElement = parentNode.querySelector('img');
    const fileInput = parentNode.querySelector('input[type="file"]');

    if (imgElement) {
        imgElement.src = '';
    }

    if (fileInput) {
        fileInput.value = '';
    }
}

function category_change(element){
    let category_id = element.value
    let product_subcategory = document.getElementById('product_subcategory')
    while (product_subcategory.firstChild) {
        product_subcategory.removeChild(product_subcategory.firstChild);
    }
    let product_name = document.getElementById('product_name')
    while (product_name.firstChild) {
        product_name.removeChild(product_name.firstChild);
    }
    fetch('profile/getsubcategories/'+category_id).then(function(res) {
        res.json().then(function(data){

            const option1 = document.createElement('option');
            option1.value = ''
            option1.textContent = ''
            product_subcategory.appendChild(option1)
            data.forEach(function(subcategory) {
            const option = document.createElement('option');
                    option.value = subcategory.id
                    option.textContent = subcategory.name;

                    product_subcategory.appendChild(option)
            })
        })
        }) 


}

function subsubcategory_change(element){
    let subcategory_id = element.value
    let product_name = document.getElementById('product_name')
    while (product_name.firstChild) {
        product_name.removeChild(product_name.firstChild);
    }
    fetch('profile/getproducts/'+subcategory_id).then(function(res) {
        res.json().then(function(data){
            console.log(data)
            data.forEach(function(product) {
            const option = document.createElement('option');
                    option.value = product.id
                    option.textContent = product.name;

                    product_name.appendChild(option)
            })
        })
        }) 


}


function file_change(element){
    element.parentElement.getElementsByClassName('file-name')[0].innerText = element.value.split('\\').reverse()[0] 
}


function clear_file(element){

    element.parentElement.childNodes.forEach(function(e){
        if(e.className == 'file-name'){
        e.innerHTML = 'No file Chosen'
    }
    })
}

function showAwardPopup(element){
    document.getElementById('addAwardPopup').style.display = "flex"

}
function closeAward(element){
    document.getElementById('addAwardPopup').style.display = "none"

}


function addAward(element){
    let table_body = document.getElementById('award_table').children[1]
    let table_length = table_body.children.length
    const newRow = document.createElement('tr');
    const nameCell = document.createElement('td');




    const deleteButton = document.createElement('button');
    deleteButton.type = 'button';
    deleteButton.style = 'background:transparent;border:none;';
    deleteButton.setAttribute('data-award-id', '123');
    deleteButton.onclick = function() {
        openDeleteAward(deleteButton);
    };

    // Create the SVG icon and append it to the button
    deleteButton.innerHTML = `




    `;
    const awardCell = document.createElement('td');
    row_cnt = Number(table_length)+1

    const award_attachment = document.createElement('input');
    award_attachment.type = 'file'
    award_attachment.id = 'award_'+row_cnt
    award_attachment.name = 'award_'+row_cnt
    const fileNameSpan = document.createElement('span');

    const originalInput  = document.getElementById('add_award');
    const add_award_name = document.getElementById('add_award_name');
    const award_name = document.getElementById('award_name');

    if (originalInput.files.length > 0) {
        const file = originalInput.files[0];
        const dataTransfer = new DataTransfer();
        dataTransfer.items.add(file);

        award_attachment.files = dataTransfer.files;
        fileNameSpan.textContent = award_name.value;

    } else {
        alert('No file selected in the original input.');
    }





    awardCell.appendChild(award_attachment)
    awardCell.appendChild(fileNameSpan);

    originalInput.value = '';
    add_award_name.textContent = 'No File Selected' 
    award_name.value = ''

    nameCell.appendChild(deleteButton);
    newRow.appendChild(nameCell);
    newRow.appendChild(awardCell);

    table_body.appendChild(newRow);

}

function updateAwardFile(inputElement) {
    let id = inputElement.id 
    id = "updated_"+id
    inputElement.name = id

    inputElement.parentElement.parentElement.children[0].replaceWith(inputElement.value.split('\\')[2]) 

}

function updateCertificateFile(inputElement) {
    let id = inputElement.id 
    id = "updated_"+id
    inputElement.name = id

    inputElement.parentElement.parentElement.children[0].replaceWith(inputElement.value.split('\\')[2]) 

}

function openDeleteAward(element){
    var awardId = element.getAttribute('data-award-id');
    console.log("Awardd",awardId)
    element.parentElement.parentElement.remove()
}
function openDeleteCertificate(element){
    let certificateId = element.getAttribute('data-certificate-id');
    console.log("Awardd",certificateId)
    element.parentElement.parentElement.remove()
}

