template.script:
    document.getElementById('company_details').style.display = 'flex'
    document.getElementById('contact_details').style.display = 'flex'
    document.getElementById('add_new_product_form').style.display = 'none'
    document.getElementById('product_management').style.display = 'none'
    document.getElementById('news_management').style.display = 'none'
    document.getElementById('edit_profile_form').style.display = 'flex'


function enable_documents(element){
    document.getElementById('documents').style.display = 'flex'
    document.getElementById('company_details').style.display = 'none'
    document.getElementById('contact_details').style.display = 'none'
    document.getElementById('add_new_product_form').style.display = 'none'



    document.querySelectorAll('.menu-item').forEach(function(item) {
            item.classList.remove('active');
        });

        // Add active class to the clicked menu item
        element.classList.add('active');
    }





